CREATE OR REPLACE FUNCTION obter_mat_med_assoc_proc_adep (nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w	bigint;
nr_seq_item_w	integer;
ds_associados_w	varchar(255);


BEGIN
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	/* obter dados horario prescricao */

	select	nr_prescricao,
		nr_seq_procedimento
	into STRICT	nr_prescricao_w,
		nr_seq_item_w
	from	prescr_proc_hor
	where	nr_sequencia = nr_seq_horario_p
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	/* obter diluicao */

	select	substr(obter_mat_med_assoc_proc(nr_prescricao_w, nr_seq_item_w, 'S'),1,255)
	into STRICT	ds_associados_w
	;

end if;

return ds_associados_w;

end;
