SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mat_med_assoc_proc ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_exibe_dose_p text) RETURNS varchar AS $body$
DECLARE


qt_dose_w			double precision;
cd_um_dose_w		varchar(30);
ds_matmed_w			varchar(60);
ds_associados_w		varchar(2000) := null;
ie_via_aplicacao_w	varchar(40);
ds_dose_w			varchar(20);

c01 CURSOR FOR
SELECT	substr(obter_desc_material(cd_material),1,60) ds_componente,
	qt_dose,
	cd_unidade_medida_dose,
	CASE WHEN coalesce(ie_via_aplicacao::text, '') = '' THEN null  ELSE ' - ' || obter_ds_ie_via_aplicacao(ie_via_aplicacao) END ||CASE WHEN coalesce(dt_suspensao::text, '') = '' THEN  null  ELSE ' - ' || UPPER(obter_desc_expressao(298991)) END
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia_proc = nr_seq_item_p
and	ie_agrupador = 5
and coalesce(dt_suspensao::text, '') = ''
order by
	1;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	open c01;
	loop
	fetch c01 into	ds_matmed_w,
			qt_dose_w,
			cd_um_dose_w,
			ie_via_aplicacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin		
			if (qt_dose_w < 1) then
			   ds_dose_w :=  '0'||qt_dose_w;
			else
			   ds_dose_w := qt_dose_w;
			end if;
			
			if (coalesce(ds_associados_w::text, '') = '') and (length(ds_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w) < 2000) then
				ds_associados_w := ds_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w;

			elsif (length(ds_associados_w) < 2000) and (length(ds_associados_w || ',' || chr(10) || ds_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w) < 2000) then
				ds_associados_w := ds_associados_w || ',' || chr(10) || ds_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w;
			end if;
		
		end;
	end loop;
	close c01;
end if;

return ds_associados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
