CREATE OR REPLACE FUNCTION obter_mat_med_disp_sobra (cd_material_p bigint, qt_dose_P bigint) RETURNS bigint AS $body$
DECLARE


cd_retorno_w	integer;

BEGIN
cd_retorno_w := 0;
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '')	and (qt_dose_P IS NOT NULL AND qt_dose_P::text <> '')	then

	select 	max(cd_material_conta)
	into STRICT	cd_retorno_w
	from	regra_baixa_sobra_quimio
	where	qt_dose_p	between qt_dose_min and qt_dose_max
	and		cd_material	=	cd_material_p;

end if;

if (coalesce(cd_retorno_w::text, '') = '') then
	cd_retorno_w 	 := 	cd_material_p;
end if;


return	cd_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
