CREATE OR REPLACE FUNCTION obter_mat_ordem_prod_rotulo ( nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_material_w	varchar(100);
cd_unid_med_w	varchar(30);
qt_dose_w	double precision;

C01 CURSOR FOR
	SELECT	substr(obter_desc_material_dcb(b.cd_material),1,100),
		qt_dose,
		cd_unidade_medida
	from	can_ordem_item_prescr b,
		material a
	where	b.nr_seq_ordem		= nr_seq_ordem_p
	and	a.cd_material		= b.cd_material
	and	a.ie_mistura		= 'S'
	and	coalesce(b.nr_sequencia_diluente::text, '') = ''
	order by nr_sequencia;


BEGIN


open C01;
loop
	fetch C01 into
		ds_material_w,
		qt_dose_w,
		cd_unid_med_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if	((coalesce(length(ds_retorno_w),0) + coalesce(length(ds_material_w),0) + coalesce(length(cd_unid_med_w),0)) < 255) then
		ds_retorno_w	:= ds_retorno_w || ds_material_w ||' '||qt_dose_w ||' '||cd_unid_med_w|| '/';
	end if;
	end;
end loop;
close C01;

ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-1);

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
