SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mat_ordem_prod_visao ( nr_seq_ordem_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_material_w		varchar(80);
ds_unid_med_w		varchar(30);
qt_dose_w		double precision;
nr_sequencia_w		bigint;
cd_material_w		integer;
ie_exibe_sem_apresent_w varchar(1);
ie_exibe_titulo_w	varchar(1);
nr_sequencia_solucao_w	integer;
ie_considerar_medic_quimio varchar(1) := 'N';
cd_estabeleciumento_w	bigint;
cd_perfil_w				bigint;
nm_usuario_w			varchar(15);
nr_seq_idioma_w			smallint;

nr_seq_atendimento_w	paciente_atend_medic.nr_seq_atendimento%type;
nr_seq_material_w		paciente_atend_medic.nr_seq_material%type;

/* ie_opcao_p C - CÃ³digo material,	 M - Material, D - dose U - unidade medida */

BEGIN
cd_estabeleciumento_w := 	wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w			  := 	obter_perfil_ativo;
nm_usuario_w		  := 	wheb_usuario_pck.get_nm_usuario;
ie_exibe_titulo_w	  :=    coalesce(quimio_parametros_pck.get_param_192,'');
nr_seq_idioma_w		  :=	philips_param_pck.get_nr_seq_idioma;


if 	((ie_exibe_titulo_w = '') or (coalesce(ie_exibe_titulo_w::text, '') = '')) then
	ie_exibe_titulo_w := Obter_Param_Usuario(3130, 192, cd_perfil_w, nm_usuario_w, cd_estabeleciumento_w, ie_exibe_titulo_w);
end if;

select	min(nr_sequencia)
into STRICT	nr_sequencia_w
from	can_ordem_item_prescr
where	nr_seq_ordem	= nr_seq_ordem_p
and	coalesce(nr_sequencia_diluente::text, '') = '';

if (ie_exibe_titulo_w = 'S') then

	select  max(ds_solucao),
		max(b.cd_material),
		max(substr(obter_unid_med_usua('ml'),1,30)),
		max(qt_solucao_total)
	into STRICT	ds_material_w,
		cd_material_w,
		ds_unid_med_w,
		qt_dose_w
	from   	prescr_solucao f,
		can_ordem_prod d,
		paciente_atend_medic b,
		prescr_material c,
		can_ordem_item_prescr x
	where  	c.nr_seq_atendimento 	= b.nr_seq_atendimento
	and    	c.nr_seq_material	= b.nr_seq_material
	and	c.nr_seq_ordem_prod  	= d.nr_sequencia
	and 	x.cd_material 		= c.cd_material
	and	d.nr_sequencia		= x.nr_seq_ordem
	and 	f.nr_prescricao		= c.nr_prescricao
	and 	c.nr_sequencia_solucao  = f.nr_seq_solucao
	and	x.nr_sequencia  	= nr_sequencia_w;
end if;


if (coalesce(ds_material_w::text, '') = '') and (coalesce(cd_material_w::text, '') = '') and (coalesce(ds_unid_med_w::text, '') = '') and (coalesce(qt_dose_w::text, '') = '') then

	ie_exibe_sem_apresent_w 	:= coalesce(quimio_parametros_pck.get_param_180,'');

	if ((ie_exibe_sem_apresent_w = '') or (coalesce(ie_exibe_sem_apresent_w::text, '') = ''))  then
		ie_exibe_sem_apresent_w := Obter_Param_Usuario(3130, 180, cd_perfil_w, nm_usuario_w, cd_estabeleciumento_w, ie_exibe_sem_apresent_w);
	end if;

	ie_considerar_medic_quimio 	:= coalesce(quimio_parametros_pck.get_param_209,'');

	if ((ie_considerar_medic_quimio = '') or (coalesce(ie_considerar_medic_quimio::text, '') = '')) then
		ie_considerar_medic_quimio := Obter_Param_Usuario(3130, 209, cd_perfil_w, nm_usuario_w, cd_estabeleciumento_w, ie_considerar_medic_quimio);
	end if;

	if (ie_exibe_sem_apresent_w = 'S') then
		select 	--max(substr(obter_desc_material(b.cd_material),1,60)),
				--max(b.cd_material),
				--max(b.cd_unid_med_prescr),
				--max(b.qt_dose_prescricao),
				min(b.nr_seq_atendimento),
				min(b.nr_seq_material)
		into	--ds_material_w,
				--cd_material_w,
				--ds_unid_med_w,
				--qt_dose_w,
				nr_seq_atendimento_w,
				nr_seq_material_w
		from   	material e,
				paciente_atend_medic b,
				prescr_material c,
				can_ordem_prod d,
				can_ordem_item_prescr a
		where  	c.nr_seq_atendimento 	= b.nr_seq_atendimento
		and    	c.nr_seq_material	= b.nr_seq_material
		and		c.nr_prescricao  	= d.nr_prescricao
		and		c.nr_seq_ordem_prod  	= d.nr_sequencia
		and 	e.cd_material		= c.cd_material
		and		((e.ie_mistura 		= 'S') or
				 ((e.ie_mistura	= 'R') and (obter_se_gera_ordem(c.ie_via_aplicacao,c.cd_intervalo, c.qt_dose, c.cd_material, c.cd_unidade_medida,'T', coalesce(c.ie_medicacao_paciente, 'N'),d.nr_atendimento) = 'S')))
		and 	a.nr_sequencia	= nr_sequencia_w
		and   	a.nr_seq_ordem = d.nr_sequencia
		and 	d.nr_sequencia 		= nr_seq_ordem_p;

		select	max(substr(obter_desc_material(b.cd_material),1,60)),
				max(b.cd_material),
				max(b.cd_unid_med_prescr),
				max(b.qt_dose_prescricao)
		into STRICT	ds_material_w,
				cd_material_w,
				ds_unid_med_w,
				qt_dose_w
		from	paciente_atend_medic b
		where	nr_seq_atendimento = nr_seq_atendimento_w
		and		nr_seq_material = nr_seq_material_w;

		if (coalesce(ds_material_w::text, '') = '') then
			Select	distinct
					max(substr(obter_desc_material(a.cd_material),1,60)),
					max(a.cd_unidade_medida),
					max(a.qt_dose),
					max(a.cd_material)
			into STRICT	ds_material_w,
					ds_unid_med_w,
					qt_dose_w,
					cd_material_w
			from	MATERIAL b,
					can_ordem_item_prescr a
			where	b.cd_material = a.cd_material
			and 	((ie_mistura = 'S') or (ie_mistura = 'R'))
			and 	a.nr_sequencia	= nr_sequencia_w;
		end if;
	elsif (ie_considerar_medic_quimio = 'S') then
		select	min(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	MATERIAL b,
			can_ordem_item_prescr a
		where	nr_seq_ordem	= nr_seq_ordem_p
		and 	b.cd_material = a.cd_material
		and 	ie_mistura = 'S'
		and	coalesce(nr_sequencia_diluente::text, '') = '';

		Select	distinct
			substr(obter_desc_material(a.cd_material),1,60),
			a.cd_unidade_medida,
			a.qt_dose,
			a.cd_material
		into STRICT	ds_material_w,
			ds_unid_med_w,
			qt_dose_w,
			cd_material_w
		from	MATERIAL b,
			can_ordem_item_prescr a
		where	b.cd_material = a.cd_material
		and 	((ie_mistura = 'S') or (ie_mistura = 'R'))
		and 	a.nr_sequencia	= nr_sequencia_w;
	else
		select	min(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	can_ordem_item_prescr
		where	nr_seq_ordem	= nr_seq_ordem_p
		and	coalesce(nr_sequencia_diluente::text, '') = '';

		Select	distinct
			substr(obter_desc_material(cd_material),1,60),
			cd_unidade_medida,
			qt_dose,
			cd_material
		into STRICT	ds_material_w,
			ds_unid_med_w,
			qt_dose_w,
			cd_material_w
		from	can_ordem_item_prescr
		where	nr_sequencia	= nr_sequencia_w;
	end if;
end if;

return	qt_dose_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_mat_ordem_prod_visao ( nr_seq_ordem_p bigint, ie_opcao_p text) FROM PUBLIC;

