SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mat_proc_princ (nr_seq_proc_princ_p bigint, ds_opcao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		double precision	:= 0;
vl_material_w		double precision;
vl_pago_w		double precision;
vl_glosado_w		double precision;


BEGIN

select	sum(vl_material) vl_material,
	sum(vl_material) - coalesce(sum(obter_valor_conv_ret_glosa(null,p.nr_sequencia,'G')),0) + coalesce(sum(obter_valor_conv_ret_glosa(null,p.nr_sequencia,'A')),0) vl_pago,
	coalesce(sum(obter_valor_conv_ret_glosa(null,p.nr_sequencia,'G')),0)
into STRICT	vl_material_w,
	vl_pago_w,
	vl_glosado_w
from	material_atend_paciente p
where	p.nr_seq_proc_princ	= nr_seq_proc_princ_p
and	coalesce(p.cd_motivo_exc_conta::text, '') = '';


if (ds_opcao_p = 'M') then
	ds_retorno_w := vl_material_w;
end if;

if (ds_opcao_p = 'P') then
	ds_retorno_w := vl_pago_w;
end if;

if (ds_opcao_p = 'G') then
	ds_retorno_w := vl_glosado_w;
end if;


RETURN ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
