CREATE OR REPLACE FUNCTION obter_max_dt_val_prescr_cpoe ( nr_seq_cpoe_p prescr_material.nr_seq_mat_cpoe%type, nr_atendimento_p prescr_medica.nr_atendimento%type) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;

BEGIN

select 	coalesce(max(a.dt_fim), max(a.dt_fim_cih))
into STRICT	dt_retorno_w
from	cpoe_material a
where	a.nr_sequencia = nr_seq_cpoe_p
and		a.nr_atendimento = nr_atendimento_p;

if (coalesce(dt_retorno_w::text, '') = '') then
	select 	max(a.dt_validade_prescr)
	into STRICT	dt_retorno_w
	from	prescr_medica a,
			prescr_material b
	where	a.nr_prescricao = b.nr_prescricao
	and		b.nr_seq_mat_cpoe = nr_seq_cpoe_p
	and		a.nr_atendimento = nr_atendimento_p;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
