CREATE OR REPLACE FUNCTION obter_max_horario_agepac ( cd_agenda_p bigint, dt_agenda_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
DH - DATA HORA pronta para insert
C    -  "Count" (Qtde) de registros encontrados com o horario informado
*/
dt_retorno_w 			timestamp:=null;
qt_agendamento_horario_w	bigint:=0;


BEGIN

select 	coalesce(count(*),0)
into STRICT	qt_agendamento_horario_w
from	agenda_paciente a
where	to_char(a.dt_agenda,'dd/mm/yyyy') 	= to_char(dt_agenda_p,'dd/mm/yyyy')
and	to_char(a.hr_inicio,'hh24:mi')		= to_char(dt_agenda_p,'hh24:mi')
and	a.cd_agenda				= cd_agenda_p;

if qt_agendamento_horario_w > 0 then
	select 	coalesce(max(a.hr_inicio)+0.00001,dt_agenda_p)
	into STRICT	dt_retorno_w
	from	agenda_paciente a
	where	to_char(a.dt_agenda,'dd/mm/yyyy') 	= to_char(dt_agenda_p,'dd/mm/yyyy')
	and	to_char(a.hr_inicio,'hh24:mi')		= to_char(dt_agenda_p,'hh24:mi')
	and	a.cd_agenda				= cd_agenda_p;
else dt_retorno_w:=dt_agenda_p;
end if;

if 	ie_opcao_p = 'C' then
	return 	to_char(qt_agendamento_horario_w);
elsif 	ie_opcao_p = 'DH' then
	return	to_char(dt_retorno_w,'dd/mm/yyyy hh24:mi:ss');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
