CREATE OR REPLACE FUNCTION obter_max_lote_trans ( nr_nota_fiscal_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w    bigint;


BEGIN
Select  max(obter_lote_trans(a.nr_sequencia))
into STRICT    nr_retorno_w
from    nfe_transmissao a,
        nfe_transmissao_nf b
where   a.nr_sequencia = b.nr_seq_transmissao
and     b.nr_seq_nota_fiscal = nr_nota_fiscal_p
and     a.ie_tipo_nota = 'NFSE';

return  nr_retorno_w;

end;
