CREATE OR REPLACE FUNCTION obter_max_mat_item_controle (cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_max_material_w	bigint;


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	select	max(a.nr_seq_item)
	into STRICT	cd_max_material_w
	from 	material_item_controle a,
		pepo_item_controle b
	where 	a.nr_seq_item 		= b.nr_sequencia
	and	coalesce(b.ie_situacao,'A') 	= 'A'
	and	a.cd_material 		= cd_material_p;
	end;
end if;

return cd_max_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
