CREATE OR REPLACE FUNCTION obter_max_prescr_hist_status ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(60);
nr_sequencia_w	bigint;

BEGIN

select	coalesce(max(nr_seq_prescr_status_hist),0)
into STRICT	nr_sequencia_w
from	prescr_procedimento_hist
where	nr_prescricao		= nr_prescricao_p
and	nr_seq_procedimento 	= nr_seq_prescr_p
and	ie_situacao 		= 'A';

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

	select	max(ds_status)
	into STRICT	ds_retorno_w
	from	prescr_proc_hist_status
	where	nr_sequencia = nr_sequencia_w;

end if;

return	ds_retorno_w;

end;
