CREATE OR REPLACE FUNCTION obter_media_aprov_ccb ( nr_seq_equipe_p bigint, dt_mes_referencia_p timestamp, ie_minutos_p text default 'N') RETURNS bigint AS $body$
DECLARE


qt_tempo_w	double precision;

BEGIN

select	coalesce(round((sum(x.qt_min_diferenca)/count(1))/ CASE WHEN ie_minutos_p='S' THEN  1  ELSE 60 END ),0) qt_media_hr
into STRICT	qt_tempo_w
from (
		SELECT 	man_obter_min_com(1,b.dt_atualizacao_nrec,b.dt_aprovacao) qt_min_diferenca
		from    man_ordem_servico a,
				man_ordem_serv_aprov_ccb b,
				ccb_equipe c,
				man_ordem_serv_impacto x
		where   a.nr_sequencia = b.nr_seq_ordem_serv
		and     c.nr_sequencia = b.nr_seq_equipe
		and     b.nr_seq_equipe	= nr_seq_equipe_p
  		and	    x.nr_sequencia = b.nr_seq_impacto
        and	    (x.dt_aprovacao IS NOT NULL AND x.dt_aprovacao::text <> '')		
		and     trunc(x.dt_atualizacao_nrec,'mm') = trunc(dt_mes_referencia_p,'mm')
		and     b.dt_aprovacao <> b.dt_atualizacao_nrec) x;

return	qt_tempo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
