CREATE OR REPLACE FUNCTION obter_media_aritmetica (ie_valores_p text, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


ds_valores_w	varchar(255);
qt_itens_w	smallint;
vl_resultado_w	double precision;


BEGIN

ds_valores_w	:= replace(ie_valores_p,' ','');
qt_itens_w 	:= length(ds_valores_w) - length(replace(ds_valores_w,'+','')) + 1;

if (ie_opcao_p = 0) then
	vl_resultado_w := obter_valor_dinamico('select (' || ds_valores_w || ')/' || qt_itens_w || ' from dual', vl_resultado_w);
else
	ds_valores_w	:= ' ' || ds_valores_w || ' ';
	ds_valores_w	:= replace(ds_valores_w,'+',' + ');
	ds_valores_w	:= replace(ds_valores_w,' 0 +','');
	ds_valores_w	:= replace(ds_valores_w,'+ 0 ','');
	ds_valores_w	:= replace(ds_valores_w,' ','');
	qt_itens_w 	:= length(ds_valores_w) - length(replace(ds_valores_w,'+','')) + 1;
	vl_resultado_w := obter_valor_dinamico('select (' || ds_valores_w || ')/' || qt_itens_w || ' from dual', vl_resultado_w);
end if;

return vl_resultado_w;

end;
