CREATE OR REPLACE FUNCTION obter_media_conhecimento_area (cd_consultor_p bigint) RETURNS bigint AS $body$
DECLARE


soma_area_w		double precision;
qt_area_w 		double precision;
vl_resultado_w		double precision;


BEGIN

select sum(pr_con_area)/CASE WHEN count(*)=0 THEN 1  ELSE count(*) END
into STRICT	vl_resultado_w
from
(SELECT	distinct vl_dominio nr_seq_area,
	substr(con_pr_conhecimento_area(cd_consultor_p, vl_dominio),1,15) pr_con_area
from	com_cons_gest_con_mod a,
	modulo_implantacao b,
	com_canal_consultor c,
	valor_dominio d
where	a.nr_seq_mod_impl = b.nr_sequencia
and	b.ie_area_tasy = d.vl_dominio
and	c.nr_sequencia = a.nr_seq_consultor
and	d.cd_dominio = 1867
and	((c.ie_tipo_consultor = 'CO' and (d.vl_dominio in (11)))
or (c.ie_tipo_consultor = 'CP') and (d.vl_dominio not in (11)))
and	c.cd_pessoa_fisica = cd_consultor_p) alias12;

return	coalesce(vl_resultado_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
