CREATE OR REPLACE FUNCTION obter_media_encerradas ( vl_media_encerrada_p bigint) RETURNS varchar AS $body$
DECLARE


resultado_w  double precision;
cor_w varchar(20);
indicador_w real := round((vl_media_encerrada_p)::numeric,2);


BEGIN

	SELECT round((to_char(clock_timestamp() - to_date('01/' || to_char(clock_timestamp(),'mm/yyyy'),'dd/mm/yyyy')) * (50 / SUBSTR(LAST_DAY(clock_timestamp()),1,2))),2)
	into STRICT resultado_w
	;

	if (indicador_w <= resultado_w ) then
		cor_w := 'RED';
	elsif (indicador_w > resultado_w ) then
		cor_w := 'GREEN';
	end if;


return	cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
