CREATE OR REPLACE FUNCTION obter_media_hr_espera (nr_seq_dia_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE

	 
qt_media_w	bigint;


BEGIN 
 
select	coalesce(trunc(sum(trunc(obter_hora_entre_datas(dt_entrada,dt_inicio_atendimento),2)) / count(*),2),0) 
into STRICT	qt_media_w 
from	atendimento_paciente 
where 	ie_tipo_atendimento = 3 
and	(dt_inicio_atendimento IS NOT NULL AND dt_inicio_atendimento::text <> '') 
and	to_char(dt_entrada, 'dd') = nr_seq_dia_p 
and	dt_entrada between dt_inicial_P and dt_final_p 
group by 	to_char(dt_entrada, 'dd');
 
return	qt_media_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
