CREATE OR REPLACE FUNCTION obter_media_inv_direto (dt_mesano_referencia_p timestamp, cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_estoque_w	double precision;
qt_movimento_w	double precision;
qt_media_w	double precision;


BEGIN

select	coalesce(sum(qt_estoque),0)
into STRICT	qt_estoque_w
from	movimento_estoque_v2 a,
	operacao_estoque b
where	a.cd_operacao_estoque	= b.cd_operacao_estoque
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.dt_mesano_referencia	= dt_mesano_referencia_p
and	b.ie_tipo_requisicao		= 5
and	a.cd_material_estoque	= cd_material_p;

select	count(nr_movimento_estoque)
into STRICT	qt_movimento_w
from	movimento_estoque_v2 a,
	operacao_estoque b
where	a.cd_operacao_estoque	= b.cd_operacao_estoque
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.dt_mesano_referencia	= dt_mesano_referencia_p
and	b.ie_tipo_requisicao	= 5
and	a.cd_material_estoque	= cd_material_p;

select	dividir(qt_estoque_w,qt_movimento_w)
into STRICT	qt_media_w
;

return	qt_media_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
