SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_media_os_por_dia_usuario ( nm_usuario_p text, qt_meses_p bigint default 0) RETURNS bigint AS $body$
DECLARE


nr_retorno_w double precision;



BEGIN

nr_retorno_w	:= 0;

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (qt_meses_p IS NOT NULL AND qt_meses_p::text <> '') then
	select  round((avg(qt_os))::numeric,1) avg_os
	into STRICT	nr_retorno_w
	from    (   SELECT	coalesce(count(distinct a.nr_seq_ordem_serv),0) qt_os
				from	man_ordem_serv_ativ a
				where	lower(a.nm_usuario_exec) = lower(nm_usuario_p)
				and		trunc(a.dt_atividade)	between trunc(clock_timestamp()) - (qt_meses_p * 30) and trunc(clock_timestamp())
				group by trunc(a.dt_atividade)
			) alias16;

end if;

return nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
