CREATE OR REPLACE FUNCTION obter_media_perc_completude (nr_seq_atend_checklist_p bigint) RETURNS bigint AS $body$
DECLARE


qt_media_completude_w	double precision;
checklist_proc_item_w	bigint;


BEGIN

select	count(1)
into STRICT	checklist_proc_item_w
from	atendimento_checklist a,
		checklist_processo_item b
where	a.nr_seq_checklist = b.nr_seq_checklist
and		a.nr_sequencia = nr_seq_atend_checklist_p;

select	trunc(dividir(sum(coalesce(x.pr_completude,0)),checklist_proc_item_w),4) pr_completude
into STRICT	qt_media_completude_w
from	med_avaliacao_paciente x
where	x.nr_sequencia in (	SELECT	max(c.nr_sequencia) nr_seq_med_aval
							from	atendimento_checklist a,
								checklist_processo_item b,
								med_avaliacao_paciente c
							where	a.nr_seq_checklist = b.nr_seq_checklist
							and	c.nr_seq_atend_checklist = a.nr_sequencia
							and	c.nr_seq_checklist_item = b.nr_sequencia
							and	coalesce(c.dt_inativacao::text, '') = ''
							and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
							and	a.nr_sequencia = nr_seq_atend_checklist_p
							group by b.nr_sequencia);


return	coalesce(qt_media_completude_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_media_perc_completude (nr_seq_atend_checklist_p bigint) FROM PUBLIC;

