SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_media_vl_unit_mat ( cd_material_p bigint, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_unit_med_w			double precision := 0;
vl_unit_w			double precision;
qt_registros_w			numeric(20);
dt_mesano_referencia_w		timestamp;


BEGIN

select	sum(coalesce(a.vl_unitario_item_nf,0)),
	coalesce(count(*),0)
into STRICT	vl_unit_w,
	qt_registros_w
from	nota_fiscal_item a,
	nota_fiscal b
where	a.cd_material = cd_material_p
and	a.nr_sequencia = b.nr_sequencia
and	to_char(b.dt_entrada_saida,'mm/yyyy') = to_char(coalesce(dt_mesano_referencia_p,clock_timestamp()),'mm/yyyy');

if	qt_registros_w > 0 then
	vl_unit_med_w := dividir(vl_unit_w,qt_registros_w);
end if;

return  vl_unit_med_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
