SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_medicamentos_mx (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		varchar(4000) 	:= '';
ds_retorno_w		varchar(4000) 	:= '';

C01 CURSOR FOR
	SELECT ccm.cd_cat_medicamento,
		   pma.nr_sequencia as nr_sequencia
	from   cat_medicamento ccm,
		   prescr_medica pme,
           prescr_material pma
	where  pme.nr_atendimento = nr_atendimento_p
	and    pme.nr_prescricao = pma.nr_prescricao
	and    pma.cd_material = ccm.cd_medicamento;

BEGIN
for r_C01_w in C01 loop
	begin
		if (coalesce(nr_sequencia_w::text, '') = '') then
				nr_sequencia_w := r_C01_w.nr_sequencia || '&' || r_C01_w.cd_cat_medicamento;
			else
				nr_sequencia_w := nr_sequencia_w || '#' || r_C01_w.nr_sequencia || '&' || r_C01_w.cd_cat_medicamento;
				ds_retorno_w :=	nr_sequencia_w;
        end if;
	end;
end loop;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
