SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_medicos_enviar_email ( ie_opcao_p text, cd_medico_cirugiao_p bigint default 0, nr_seq_agenda_p bigint default 0) RETURNS varchar AS $body$
DECLARE



/*ie_opcao					
Medicos com agendamento para p dia seguinte - S*/
ds_email_w		varchar(255);
ds_lista_email_w	varchar(4000);

c01 CURSOR FOR
	SELECT	distinct
		obter_dados_pf_pj(a.cd_medico,null,'M')
	from	agenda_paciente a,
			agenda b
	where	establishment_timezone_utils.startofday(a.dt_agenda) = establishment_timezone_utils.startofday(clock_timestamp()) +1
	and	(obter_dados_pf_pj(a.cd_medico,null,'M') IS NOT NULL AND (obter_dados_pf_pj(a.cd_medico,null,'M'))::text <> '')
	and	ie_opcao_p = 'S'
	and b.cd_agenda = a.cd_agenda
	and b.cd_tipo_agenda = 1
	
union
 
	SELECT	distinct
		obter_dados_pf_pj(a.cd_medico,null,'M')
	from	agenda_paciente a, 
			agenda b
	where	(obter_dados_pf_pj(a.cd_medico,null,'M') IS NOT NULL AND (obter_dados_pf_pj(a.cd_medico,null,'M'))::text <> '')
	and	ie_opcao_p = 'P'
	and 	cd_medico = cd_medico_cirugiao_p
	and b.cd_agenda = a.cd_agenda
	and b.cd_tipo_agenda = 1
	
union

	select	distinct
		ds_email_comunicacao
	from 	agenda_pac_equip b, 
		equipamento a 
	where 	a.cd_equipamento = b.cd_equipamento
	and 	b.nr_seq_agenda	 = nr_seq_agenda_p
	and 	(ds_email_comunicacao IS NOT NULL AND ds_email_comunicacao::text <> '')
	and	ie_opcao_p = 'C';

BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ds_email_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ds_lista_email_w IS NOT NULL AND ds_lista_email_w::text <> '') then	
		ds_lista_email_w := substr(ds_lista_email_w || '; ',1,4000);
	end if;
		
	ds_lista_email_w	:= substr(ds_lista_email_w || ds_email_w,1,4000);	

	end;
END LOOP;
CLOSE C01;

return ds_lista_email_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_medicos_enviar_email ( ie_opcao_p text, cd_medico_cirugiao_p bigint default 0, nr_seq_agenda_p bigint default 0) FROM PUBLIC;

