CREATE OR REPLACE FUNCTION obter_medicos_prescr ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE

				
cd_medico_solic_w	varchar(10);
nm_medico_concat_w	varchar(2000);
nm_medico_solic_w	varchar(100);

c01 CURSOR FOR
SELECT	distinct coalesce(a.cd_medico_solicitante,'0')
from	prescr_procedimento a
where 	a.nr_prescricao = nr_prescricao_p
and		a.nr_sequencia	= nr_seq_prescr_p;

c02 CURSOR FOR
SELECT	distinct coalesce(b.cd_medico_solicitante,'0')
from	prescr_medica_prof_adic b,
		prescr_medica a
where	a.nr_prescricao = b.nr_prescricao
and		a.nr_prescricao = nr_prescricao_p;

c03 CURSOR FOR
SELECT	distinct coalesce(b.cd_profissional,'0')
from	prescr_proced_prof_adic b,
		prescr_procedimento a
where	a.nr_prescricao = b.nr_prescricao
and		a.nr_sequencia  = b.nr_seq_procedimento
and		a.nr_prescricao = nr_prescricao_p
and		a.nr_sequencia = nr_seq_prescr_p;


BEGIN

open c01;
loop
fetch c01 into
	cd_medico_solic_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (cd_medico_solic_w <> '0') then
		select	max(SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,100))
		into STRICT	nm_medico_solic_w
		from 	pessoa_fisica
		where	cd_pessoa_fisica = cd_medico_solic_w;
		
		nm_medico_concat_w := nm_medico_concat_w||nm_medico_solic_w||', ';
	end if;
	
end loop;
close c01;

open c02;
loop
fetch c02 into
	cd_medico_solic_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	if (cd_medico_solic_w <> '0') then
		select	max(SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,100))
		into STRICT	nm_medico_solic_w
		from 	pessoa_fisica
		where	cd_pessoa_fisica = cd_medico_solic_w;
		
		nm_medico_concat_w := nm_medico_concat_w||nm_medico_solic_w||', ';
	end if;
	
end loop;
close c02;

open c03;
loop
fetch c03 into
	cd_medico_solic_w;
EXIT WHEN NOT FOUND; /* apply on c03 */
	if (cd_medico_solic_w <> '0') then
		select	max(SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,100))
		into STRICT	nm_medico_solic_w
		from 	pessoa_fisica
		where	cd_pessoa_fisica = cd_medico_solic_w;
		
		nm_medico_concat_w := nm_medico_concat_w||nm_medico_solic_w||', ';
	end if;
	
end loop;
close c03;

return	substr(nm_medico_concat_w,1,length(nm_medico_concat_w)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
