CREATE OR REPLACE FUNCTION obter_medicos_procedimento ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
ds_retorno_w				varchar(2000);
nm_medico_w				varchar(200);
ds_funcao_w				varchar(200);

c01 CURSOR FOR 
	SELECT	SUBSTR(obter_nome_pessoa_fisica(b.cd_pessoa_fisica,null),1,100) nm_medico, 
		substr(obter_funcao_medico(b.ie_funcao),1,60) ds_funcao_medico 
	from 	procedimento_participante	b, 
		procedimento_paciente	a 
	where	a.nr_sequencia		= nr_sequencia_p 
	and	a.nr_sequencia 		= b.nr_sequencia;
				

BEGIN 
 
ds_retorno_W:= '';
 
begin 
select	SUBSTR(coalesce(obter_nome_pessoa_fisica(cd_medico_executor,null),wheb_mensagem_pck.get_texto(305785)),1,100), 
	substr(coalesce(obter_funcao_medico(ie_funcao_medico),wheb_mensagem_pck.get_texto(308066)),1,60) 
into STRICT	nm_medico_w, 
	ds_funcao_w 
from 	procedimento_paciente 
where 	nr_sequencia = nr_sequencia_p;
exception 
	when others then 
	nm_medico_w:= '';
	ds_funcao_w:= '';
end;
 
ds_retorno_W:= ds_funcao_w || ': ' || nm_medico_w || '; ';
 
open c01;
loop 
fetch c01 into	nm_medico_w, 
		ds_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w	:=	ds_retorno_w || ds_funcao_w || ': ' || nm_medico_w || '; ';
end loop;
 
close c01;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
