CREATE OR REPLACE FUNCTION obter_medico_agenda_prescr (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
nr_atendimento_w	bigint;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then

	select	max(a.nr_atendimento)
	into STRICT	nr_atendimento_w
	from	prescr_medica a
	where	a.nr_prescricao = nr_prescricao_p;

	if (coalesce(nr_atendimento_w,0) > 0) then

		select	max(b.cd_medico)
		into STRICT	cd_pessoa_fisica_w
		from	cirurgia a,
			agenda_paciente b
		where	b.nr_cirurgia = a.nr_cirurgia
		and	a.nr_prescricao in (	SELECT	c.nr_prescricao
						from	prescr_medica c
						where	c.nr_atendimento = nr_atendimento_w);

	end if;
end if;

return	cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_medico_agenda_prescr (nr_prescricao_p bigint) FROM PUBLIC;

