CREATE OR REPLACE FUNCTION obter_medico_alta (nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				pessoa_fisica.ds_codigo_prof%type;
nm_usuario_w				atendimento_paciente.nm_usuario_alta_medica%type;
ie_usuario_alta_medico_w	varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	obter_se_usuario_medico(coalesce(a.nm_usuario_alta_medica, a.nm_usuario_alta)),
			coalesce(a.nm_usuario_alta_medica, a.nm_usuario_alta)
	into STRICT	ie_usuario_alta_medico_w,
			nm_usuario_w
	from	atendimento_paciente a
	where 	a.nr_atendimento = nr_atendimento_p;

	if (ie_usuario_alta_medico_w = 'S') then

		select 	a.ds_codigo_prof
		into STRICT	ds_retorno_w
		from	pessoa_fisica a
		where	a.cd_pessoa_fisica = Obter_Pf_Usuario(nm_usuario_w, 'C');

	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
