CREATE OR REPLACE FUNCTION obter_medico_ciente (nr_atend_alta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w char(1);


BEGIN

	select max(b.ie_medico_ciente)
	into STRICT   ie_retorno_w
	from   atendimento_paciente a,
		   atendimento_alta b,
		   parametro_medico p
	where  a.nr_atend_alta = b.nr_atendimento
	and    a.nr_atendimento = nr_atend_alta_p
	and    p.cd_estabelecimento = obter_estabelecimento_ativo
	and    ((b.ie_tipo_orientacao <> 'P')
	or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
	or     ((b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and (coalesce(b.dt_inativacao::text, '') = '')));

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
