CREATE OR REPLACE FUNCTION obter_medico_cirurgia ( nr_cirurgia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao_p	 
	C - Código 
	D - Descrição 
*/
 
 
ds_retorno_w		varchar(150);
cd_medico_exc_w		varchar(10);


BEGIN 
 
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then 
	select	cd_medico_cirurgiao 
	into STRICT	cd_medico_exc_w 
	from	cirurgia 
	where	nr_cirurgia	= nr_cirurgia_p;
 
	if (ie_opcao_p	= 'C') then 
		ds_retorno_w	:= cd_medico_exc_w;
	elsif (ie_opcao_p	= 'D') then 
		ds_retorno_w	:= obter_nome_medico(cd_medico_exc_w,'C');
	end if;
	 
end if;
 
return ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
