CREATE OR REPLACE FUNCTION obter_medico_consulta_agepac (nr_seq_agenda_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		pessoa_fisica.nm_pessoa_fisica%type;
cd_profissional_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

begin
  select cd_profissional
  into STRICT	cd_profissional_w
  from (SELECT cd_profissional
  	FROM profissional_agenda xx
LEFT OUTER JOIN funcao_medico yy ON (xx.cd_funcao = yy.cd_funcao)
WHERE xx.nr_seq_agenda = nr_seq_agenda_p order by case when yy.ie_cirurgiao = 'S'
  		then 1
  		else 2
  	end) a LIMIT 1;
exception
when others then
  cd_profissional_w := null;
end;

if (cd_profissional_w IS NOT NULL AND cd_profissional_w::text <> '') then
	if (ie_tipo_retorno_p = 'N') then
		ds_retorno_w := obter_nome_pf(cd_profissional_w);
	else
		ds_retorno_w := cd_profissional_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
