CREATE OR REPLACE FUNCTION obter_medico_diagnostico ( nr_atendimento_p bigint, dt_diagnostico_p timestamp, ie_codigo_nome_p text) RETURNS varchar AS $body$
DECLARE

 
cd_medico_w	varchar(10);
nm_medico_w	varchar(60);
ds_retorno_w	varchar(60);


BEGIN 
 
select	cd_medico, 
	substr(obter_nome_pf(cd_medico),1,60) 
into STRICT	cd_medico_w, 
	nm_medico_w 
from	diagnostico_medico 
where	nr_atendimento	= nr_atendimento_p 
and	dt_diagnostico	= dt_diagnostico_p;
 
if (ie_codigo_nome_p = 'N') then 
	ds_retorno_w	:= nm_medico_w;
else 
	ds_retorno_w	:= cd_medico_w;
end if;
 
return ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
