CREATE OR REPLACE FUNCTION obter_medico_exec_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
cd_grupo_proc_w		bigint;
cd_medico_exec_w	varchar(50);
cd_tipo_procedimento_w  smallint;

c01 CURSOR FOR
SELECT	cd_medico_exec
from	rep_medico_exec_proc b
where	coalesce(cd_procedimento, cd_procedimento_p)	= cd_procedimento_p
and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced	= ie_origem_proced_p))
and	coalesce(cd_area_procedimento, cd_area_procedimento_w) = cd_area_procedimento_w
and	coalesce(cd_especialidade, cd_especialidade_w)	= cd_especialidade_w
and	coalesce(cd_grupo_proc, cd_grupo_proc_w)		= cd_grupo_proc_w
and coalesce(cd_tipo_procedimento, cd_tipo_procedimento_w) = cd_tipo_procedimento_w
order by coalesce(b.cd_procedimento,0),
	coalesce(b.cd_grupo_proc,0),
	coalesce(b.cd_especialidade,0),
	coalesce(b.cd_area_procedimento,0),
	coalesce(b.cd_tipo_procedimento,0);


BEGIN

select	coalesce(max(cd_area_procedimento),0),
	coalesce(max(cd_especialidade),0),
	coalesce(max(cd_grupo_proc),0),
	coalesce(max(cd_tipo_procedimento), 0)
into STRICT	cd_area_procedimento_w,
	cd_especialidade_w,
	cd_grupo_proc_w,
	cd_tipo_procedimento_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

open C01;
loop
fetch C01 into
	cd_medico_exec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	cd_medico_exec_w	:= cd_medico_exec_w;
end loop;
close C01;

return	cd_medico_exec_w;

end;
$body$
