CREATE OR REPLACE FUNCTION obter_medico_ficha_financeira ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
ds_nome_medico_w	varchar(255);

C01 CURSOR FOR
	SELECT	substr(ds_informacao, 1, 255) ds_nome_medico
	from	pessoa_fisica_cobr_financ
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_tipo_informacao = 'MR'
	order by ds_nome_medico;

BEGIN

open C01;
loop
fetch C01 into
	ds_nome_medico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_nome_medico_w;
	else
		ds_retorno_w :=	ds_retorno_w || ', ' || ds_nome_medico_w;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
