CREATE OR REPLACE FUNCTION obter_medico_laudo_sequencia (nr_seq_laudo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
C - CÃ³digo;
N - Nome;
*/
cd_medico_w	varchar(10);
ds_retorno_w	varchar(60);


BEGIN
if (nr_seq_laudo_p IS NOT NULL AND nr_seq_laudo_p::text <> '') then
	select	max(l.cd_medico_resp)
	into STRICT	cd_medico_w
	from	laudo_paciente l
	where	l.nr_sequencia = nr_seq_laudo_p;

	if (ie_opcao_p = 'N') then
		ds_retorno_w	:= substr(obter_nome_pf(cd_medico_w),1,60);
	else
		ds_retorno_w	:= cd_medico_w;
	end if;
end if;

RETURN	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
