CREATE OR REPLACE FUNCTION obter_medico_para_diag ( nr_atendimento_p bigint, ie_tipo_atend_p bigint) RETURNS varchar AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	cd_medico_atual 
	from	atendimento_troca_medico 
	where	nr_atendimento		= nr_atendimento_p 
	and	ie_tipo_atendimento	= ie_tipo_atend_p 
	order by dt_troca;	
 
cd_medico_atual_w	varchar(10);


BEGIN	 
 
open C01;
loop 
fetch C01 into	 
	cd_medico_atual_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	cd_medico_atual_w	:= cd_medico_atual_w;
	end;
end loop;
close C01;
 
if (coalesce(cd_medico_atual_w::text, '') = '') then 
	select	cd_medico_resp 
	into STRICT	cd_medico_atual_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_p;
end if;
 
return	cd_medico_atual_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
