CREATE OR REPLACE FUNCTION obter_medico_parecer (nr_parecer_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

nm_medico_parecer_w		varchar(100);
/* 
ie_opcao_p - parâmetros: 
'R' médico requisitante 
'C'médico consultor 
 */
BEGIN 
if (ie_opcao_p = 'R') then 
	select 	obter_nome_medico(cd_medico, 'N') 
	into STRICT	nm_medico_parecer_w 
	from	parecer_medico_req 
	where	nr_parecer = nr_parecer_p;	
elsif (ie_opcao_p = 'C') then 
	select 	obter_nome_medico(cd_medico, 'N') 
	into STRICT	nm_medico_parecer_w 
	from	parecer_medico 
	where	nr_parecer = nr_parecer_p;	
else 
	nm_medico_parecer_w := '';
end if;
 
return nm_medico_parecer_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
