CREATE OR REPLACE FUNCTION obter_medico_participante (nr_sequencia_p bigint, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w varchar(60);


BEGIN 
 
if (cd_funcao_p = '9') then 
	select	max(SUBSTR(obter_nome_pessoa_fisica(a.cd_pessoa_fisica,null),1,60)) 
	into STRICT	ds_retorno_w 
	from	procedimento_participante a, 
		funcao_medico  b 
	where	a.nr_sequencia = nr_sequencia_p 
	and	cd_funcao_p = a.ie_funcao 
	and	b.ie_anestesista = 'S';
elsif (cd_funcao_p = '2') then 
	select	max(SUBSTR(obter_nome_pessoa_fisica(a.cd_pessoa_fisica,null),1,60)) 
	into STRICT	ds_retorno_w 
	from	procedimento_participante a, 
		funcao_medico  b 
	where	a.nr_sequencia = nr_sequencia_p 
	and	cd_funcao_p = a.ie_funcao 
	and	b.ie_auxiliar = 'S';
elsif (cd_funcao_p = '4') then 
	select	max(SUBSTR(obter_nome_pessoa_fisica(a.cd_pessoa_fisica,null),1,60)) 
	into STRICT	ds_retorno_w 
	from	procedimento_participante a, 
		funcao_medico  b 
	where	a.nr_sequencia = nr_sequencia_p 
	and	cd_funcao_p = a.ie_funcao 
	and	b.ie_auxiliar = 'S';
end if;
 
return ds_retorno_w;
 
end;
$body$
