CREATE OR REPLACE FUNCTION obter_medico_por_exame_regra (cd_procedimento_p bigint, ie_origem_proced_p text, nr_seq_proc_interno_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE


retorno_w		bigint;
nr_seq_horario_w	bigint;


BEGIN

select 	max(nr_seq_horario)
into STRICT   	nr_seq_horario_w
from   	agenda_paciente
where  	cd_agenda = cd_agenda_p
and	trunc(dt_agenda) = trunc(dt_agenda_p)
and	to_char(hr_inicio, 'hh24:mi') = to_char(dt_agenda_p, 'hh24:mi');

select	coalesce(max(a.cd_medico),0)
into STRICT 	retorno_w
from	agenda_medico_proced a,
	agenda_horario b
where	a.cd_agenda = b.cd_agenda
and	((a.cd_procedimento = cd_procedimento_p) or (coalesce(a.cd_procedimento::text, '') = ''))
and	((coalesce(a.cd_procedimento::text, '') = '') or ((a.ie_origem_proced = ie_origem_proced_p) or (coalesce(a.ie_origem_proced::text, '') = '')))
and	((a.nr_seq_proc_interno = nr_seq_proc_interno_p) or (coalesce(a.nr_seq_proc_interno::text, '') = ''))
and	((a.cd_area_procedimento = obter_area_procedimento(cd_procedimento_p, ie_origem_proced_p)) or (coalesce(a.cd_area_procedimento::text, '') = ''))
and	((a.cd_especialidade = obter_especialidade_proced(cd_procedimento_p, ie_origem_proced_p)) or (coalesce(a.cd_especialidade::text, '') = ''))
and	((a.cd_grupo_proc = obter_grupo_procedimento(cd_procedimento_p, ie_origem_proced_p,'C')) or (coalesce(a.cd_grupo_proc::text, '') = ''))
and	(a.cd_medico = (SELECT 	max(x.cd_medico)
		        from	agenda_medico x
		        where	x.nr_sequencia = b.nr_seq_medico_exec) or (coalesce(b.nr_seq_medico_exec::text, '') = ''))
and	a.cd_agenda = cd_agenda_p
and	b.nr_sequencia = nr_seq_horario_w;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
