SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_medico_refer_atend ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(100);
nm_medico_resp_w	varchar(100);
cd_medico_referido_w	varchar(10);


BEGIN 
 
select	cd_medico_referido, 
	SUBSTR(OBTER_NOME_PF(b.CD_PESSOA_FISICA),0,100) 
into STRICT	cd_medico_referido_w, 
	nm_medico_resp_w 
from	pessoa_fisica b, 
	atendimento_paciente a 
where	a.nr_atendimento	= nr_atendimento_p 
and	a.cd_medico_referido	= b.cd_pessoa_fisica;
 
if (ie_opcao_p = 'C') then 
	ds_retorno_w	:= cd_medico_referido_w;
elsif (ie_opcao_p = 'N') then 
	ds_retorno_w	:= nm_medico_resp_w;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
