CREATE OR REPLACE FUNCTION obter_medico_resp_atend ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
cd_medico_resp_w	varchar(10);
nm_medico_resp_w	varchar(100);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p <> 0) then

	select	cd_medico_resp,
		SUBSTR(OBTER_NOME_PF(b.CD_PESSOA_FISICA),0,100)
	into STRICT	cd_medico_resp_w,
		nm_medico_resp_w
	from	pessoa_fisica b,
		atendimento_paciente a
	where	a.nr_atendimento	= nr_atendimento_p
	and	a.cd_medico_resp	= b.cd_pessoa_fisica;

end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_medico_resp_w;
elsif (ie_opcao_p = 'N') then
	ds_retorno_w	:= nm_medico_resp_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
