SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_medico_validation_info ( nr_sequencia_p bigint, ie_info_type_p text) RETURNS varchar AS $body$
DECLARE




ds_retorno_w		varchar(255);



-- ie_info_type_p   S - Validate by Physician


--                  D - Date/Time of validation done by physican
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

Begin



If ( ie_info_type_p = 'S') then

Begin

select max(obter_nome_medico(cd_medico,'N'))

  into STRICT ds_retorno_w

	from	 CPOE_ACTION_LOG_V

	where	 nr_sequencia 	= nr_sequencia_p

  and  (cd_medico IS NOT NULL AND cd_medico::text <> '')

  and  (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')

  and IE_ORDERED_ON_BEHALF ='S';

  end;

end if;

If ( ie_info_type_p = 'D') then

begin

  select max(DT_CIENCIA_MEDICO) into STRICT ds_retorno_w

	from	 CPOE_ACTION_LOG_V

	where	 nr_sequencia 	= nr_sequencia_p

  and  (cd_medico IS NOT NULL AND cd_medico::text <> '')

  and IE_ORDERED_ON_BEHALF ='S'

  and  (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')

  and (DT_CIENCIA_MEDICO IS NOT NULL AND DT_CIENCIA_MEDICO::text <> '');

  end;

end if;

end;

end if;



RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
