CREATE OR REPLACE FUNCTION obter_medic_comerc_gener (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);

ie_tipo_w		char(1);
ds_material_w		varchar(100);
ds_materiais_w		varchar(2000);

c01 CURSOR FOR
	SELECT	substr(ds_reduzida,1,100)
	from	material
	where	cd_material_generico	= obter_material_generico(cd_material_p)
	and	cd_material 		<> cd_material_p
	and	ie_situacao		= 'A'
	order by
		ie_situacao desc,
		coalesce(qt_prioridade_coml,0),
		coalesce(ie_padronizado,'N');


BEGIN

select	ie_tipo_material
into STRICT	ie_tipo_w
from	material
where	cd_material	= cd_material_p;

if (ie_tipo_w = '3') then

	open C01;
	loop
	fetch C01 into
		ds_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			if (length(ds_materiais_w) < 2000) or (coalesce(ds_materiais_w::text, '') = '') then
				ds_materiais_w := ds_materiais_w || ', ' || ds_material_w;
			end if;
		end;
	end loop;
	close C01;
	ds_materiais_w 	:= substr(ds_materiais_w, 3,(length(ds_materiais_w))) || '.';
	ds_retorno_w	:= substr(ds_materiais_w,1,2000);

elsif (ie_tipo_w = '2') and (cd_material_p <> obter_material_generico(cd_material_p)) then

	select	substr(ds_reduzida,1,100)
	into STRICT	ds_retorno_w
	from	material
	where	cd_material = obter_material_generico(cd_material_p)
	and	ie_situacao		= 'A';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
