CREATE OR REPLACE FUNCTION obter_medic_hist_saude_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_lista_medicamentos_w varchar(2000);
ds_medicamento_w 	varchar(255);

c01 CURSOR FOR
SELECT	coalesce(b.ds_material,a.ds_medicamento)
FROM paciente_medic_uso a
LEFT OUTER JOIN material b ON (a.cd_material = b.cd_material)
WHERE a.nr_atendimento	= nr_atendimento_p and coalesce(a.dt_inativacao::text, '') = '' and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and ((a.ds_medicamento IS NOT NULL AND a.ds_medicamento::text <> '') or (b.ds_material IS NOT NULL AND b.ds_material::text <> '')) and ((coalesce(a.dt_fim::text, '') = '') or (clock_timestamp() between coalesce(a.dt_inicio,clock_timestamp() - interval '1 days') and a.dt_fim + 86399/86400));


BEGIN

open C01;
	loop
	fetch C01 into
		ds_medicamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		if (ds_lista_medicamentos_w IS NOT NULL AND ds_lista_medicamentos_w::text <> '') then
			ds_lista_medicamentos_w := substr(ds_lista_medicamentos_w || ' , ',1,2000);
		end if;
		ds_lista_medicamentos_w := substr(ds_lista_medicamentos_w || ds_medicamento_w,1,2000);

	end loop;
close C01;

return	ds_lista_medicamentos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
