SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_medic_micromedex_html ( cd_gfc_p text, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

nm_medicamento_w		varchar(4000);
ds_retorno_w		varchar(4000);
primeiro_registro_w	varchar(1);

c01 CURSOR FOR
SELECT distinct coalesce(upper(obter_desc_material(cd_material)), '') nm_medicamento
from	prescr_material
where	cd_material in (select	cd_interno
		        from		conversao_meio_externo
		        where	nm_tabela	= 'MATERIAL'
		        and		nm_atributo	= 'CD_MATERIAL'
		        and		ie_sistema_externo	= 'MDX'
		        and		cd_externo	= cd_gfc_p)
and nr_prescricao = nr_prescricao_p;


BEGIN
ds_retorno_w := '';

if (cd_gfc_p IS NOT NULL AND cd_gfc_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	primeiro_registro_w := 'S';

	open c01;
	loop
	fetch c01 into nm_medicamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (primeiro_registro_w = 'N') then
		   ds_retorno_w := ds_retorno_w || '<br>';
		else
		   primeiro_registro_w := 'N';
		end if;

		ds_retorno_w := ds_retorno_w || nm_medicamento_w;
		end;
	end loop;
	close c01;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
