SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_medic_principal_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


cd_material_w		prescr_material.cd_material%type;
nr_seq_cpoe_w		cpoe_material.nr_sequencia%type;
nr_seq_material_w	prescr_material.nr_sequencia%type;


BEGIN

select	max(a.nr_seq_mat_cpoe)
into STRICT	nr_seq_cpoe_w
from	prescr_material a
where	a.nr_prescricao = nr_prescricao_p
and		a.nr_sequencia_solucao = nr_seq_solucao_p;

if (coalesce(nr_seq_cpoe_w::text, '') = '') then

	select	max(a.cd_material)
	into STRICT	cd_material_w
	from	prescr_material a,
			material b
	where	a.nr_prescricao = nr_prescricao_p
	and		a.nr_sequencia_solucao = nr_seq_solucao_p
	and		a.cd_material = b.cd_material
	and		coalesce(b.ie_ancora_solucao,'N') = 'S';

	if (coalesce(cd_material_w::text, '') = '') then

		select	min(a.nr_sequencia)
		into STRICT	nr_seq_material_w
		from	prescr_material a
		where	a.nr_prescricao = nr_prescricao_p
		and		a.nr_sequencia_solucao = nr_seq_solucao_p;

		select	max(a.cd_material)
		into STRICT	cd_material_w
		from	prescr_material a
		where	a.nr_prescricao = nr_prescricao_p
		and		a.nr_sequencia_solucao = nr_seq_solucao_p
		and		a.nr_sequencia = nr_seq_material_w;

	end if;

else

	select	max(a.cd_material)
	into STRICT	cd_material_w
	from	cpoe_material a
	where	a.nr_sequencia = nr_seq_cpoe_w;

end if;

return	cd_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
