CREATE OR REPLACE FUNCTION obter_med_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
C01 CURSOR FOR 
	SELECT	CASE WHEN  b.cd_medico_anterior=384917 THEN  ' '  ELSE substr(obter_nome_pessoa_fisica(b.cd_medico_anterior,null),1,255) END  
	from	atendimento_troca_medico b 
	where	nr_atendimento = nr_atendimento_p 
	order by 1;				
ds_retorno_w		varchar(2000);
ds_med_anterior_w	varchar(1000);

BEGIN
 
open C01;
loop 
fetch C01 into	 
	ds_med_anterior_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(ds_retorno_w::text, '') = '') then 
		ds_retorno_w := ds_retorno_w || ds_med_anterior_w;
	else 
		ds_retorno_w := ds_retorno_w ||', '||ds_med_anterior_w;
	end if;
	end;
end loop;
close C01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
