SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_med_brasindice ( CD_ESTABELECIMENTO_P bigint, CD_MATERIAL_P bigint, DT_VIGENCIA_P timestamp, IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE



cd_medicamento_w		varchar(06);
dt_vigencia_w			timestamp;

ds_medicamento_w		varchar(80) 	:= '';

C01 CURSOR FOR
	SELECT	CD_MEDICAMENTO
	FROM	MATERIAL_BRASINDICE
	WHERE	CD_MATERIAL						= CD_MATERIAL_P
	and	coalesce(IE_SITUACAO, 'A')					= 'A'
	and	coalesce(dt_vigencia,dt_vigencia_w)				<= dt_vigencia_w
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p, 0))	= coalesce(cd_estabelecimento_p, 0)
	order by coalesce(dt_vigencia,clock_timestamp() - interval '1000 days');


BEGIN

DT_VIGENCIA_W 	:= coalesce(DT_VIGENCIA_P,clock_timestamp());

OPEN C01;
LOOP
	FETCH C01 into	cd_medicamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	cd_medicamento_w			:= cd_medicamento_w;
END LOOP;
CLOSE C01;


if (ie_opcao_p = 'C') then
	Return cd_medicamento_w;
else
	if (cd_medicamento_w IS NOT NULL AND cd_medicamento_w::text <> '') then
		select coalesce(max(ds_medicamento), wheb_mensagem_pck.get_texto(799513,'CD_MEDICAMENTO=' || cd_medicamento_w))
		into STRICT ds_medicamento_w
		from brasindice_medicamento
		where cd_medicamento 		= cd_medicamento_w;
	end if;

	Return ds_medicamento_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
