SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_med_conversao_compra ( cd_material_p bigint, cd_cgc_p text, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(25);
qt_conv_fornec_w			bigint;
qt_conv_compra_w			double precision;
cd_medida_compra_w		varchar(25);



BEGIN

select	count(*)
into STRICT	qt_conv_fornec_w
from	material_fornec
where	cd_cgc			= cd_cgc_p
and	cd_material		= cd_material_p
and	coalesce(ie_ressuprimento,'N') 	= 'S';

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') and (qt_conv_fornec_w > 0) then
	begin

	select	cd_unid_medida,
		qt_conv_compra_est
	into STRICT	cd_medida_compra_w,
		qt_conv_compra_w
	from	material_fornec
	where	cd_cgc = cd_cgc_p
	and	cd_material = cd_material_p
	and	coalesce(ie_ressuprimento,'N') = 'S'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	nr_sequencia = (
		SELECT	max(x.nr_sequencia)
		from	material_fornec x
		where	x.cd_material = cd_material_p
		and	x.cd_cgc = cd_cgc_p
		and	x.cd_estabelecimento = cd_estabelecimento_p
		and	coalesce(x.ie_ressuprimento,'N') = 'S');
	end;
end if;

if ((qt_conv_fornec_w = 0) or (coalesce(cd_cgc_p::text, '') = '')) then
	begin

	select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,255) cd_unidade_medida_compra,
		qt_conv_compra_estoque
	into STRICT	cd_medida_compra_w,
		qt_conv_compra_w
	from	material
	where	cd_material = cd_material_p;

	end;

end if;

if (ie_opcao_p = 'Q') then
	ds_retorno_w := qt_conv_compra_w;
elsif (ie_opcao_p = 'U') then
	ds_retorno_w := cd_medida_compra_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
