CREATE OR REPLACE FUNCTION obter_med_dominio_concatenado (nr_seq_dominio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_valor_dominio_w			varchar(255) := '';
ds_valor_dominio_ww			varchar(255);

C01 CURSOR FOR
SELECT	ds_valor_dominio
from	med_valor_dominio
where	nr_seq_dominio = nr_seq_dominio_p
order by 1;


BEGIN

open C01;
	loop
	fetch c01 into	ds_valor_dominio_ww;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_valor_dominio_w := ds_valor_dominio_w||ds_valor_dominio_ww||';';
		end;
	end loop;
	close c01;

return	ds_valor_dominio_w;

end;
