CREATE OR REPLACE FUNCTION obter_med_externo_agenda_exame (nr_seq_agenda_p bigint, ie_consulta_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nm_medico_w		varchar(255);
nr_crm_w		varchar(255);


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_consulta_p = 'S') then


	select 	max(nm_medico_externo),
			max(crm_medico_externo)
	into STRICT	nm_medico_w,
			nr_crm_w
	from 	agenda_consulta
	where 	nr_sequencia = nr_seq_agenda_p;

end if;

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_consulta_p = 'N') then

	select	max(nm_medico_externo),
			max(crm_medico_externo)
	into STRICT	nm_medico_w,
			nr_crm_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

end if;

ds_retorno_w := nr_crm_w || '-' || nm_medico_w;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
