CREATE OR REPLACE FUNCTION obter_med_micromedex_angular (cd_gfc_p text, nr_sequencia_p bigint, cd_material_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(4000) := '';


BEGIN

  ds_retorno_w := '';

	if (cd_gfc_p IS NOT NULL AND cd_gfc_p::text <> '') then

			begin
				select  max(upper(obter_desc_material(cd_material))) nm_medicamento
				into STRICT    ds_retorno_w
				from	material_conversao
				where	gfc_code = cd_gfc_p
				and (cd_material = coalesce(cd_material_p,cd_material));
			exception
			when others then
				ds_retorno_w := '';
			end;	
	end if;
	
  return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
